import React, { Component } from 'react';
import SiteHeader from './SiteHeader';
import CollapsiblePanel from './CollapsiblePanel';
import Form from './form';
import axios from 'axios';
import spinner from './loader.svg';

class PageComponent extends Component {
    state = {
            linkLinks: [],
            imageLinks: [],
            isLoading: false
    }
    toggleSpinner = () => {
        this.setState({...this.state, isLoading: !this.state.isLoading});
    }

    makeCrawlResuest = (url, numberOfLevels) => {
        var req_payload = {
            url: [url],
            numberOfLevels: [numberOfLevels.toString()]
        }
        this.toggleSpinner();
        axios.post('http://localhost:8000/crawler/crawl/', req_payload)
            .then(res => {
                this.toggleSpinner();
                this.setState({...this.state, linkLinks: res.data.links.links, imageLinks:res.data.links.images});
            })
    }

    render() {
        return (
            <div>
                <SiteHeader></SiteHeader>
                <Form makeCrawlResuest={this.makeCrawlResuest}/>
                    
                    {this.state.isLoading ? (
                        <div className="spinner-div">
                            <img src={spinner} />
                        </div>
                    ) : (
                        <div className='padding-20'>
                            {this.state.linkLinks.length > 0 && (
                                <CollapsiblePanel header='Links' linkList={this.state.linkLinks} content='links' />)}
                            
                            {this.state.imageLinks.length > 0 && (
                            <CollapsiblePanel header='Images' linkList={this.state.imageLinks} content='links' />)}
                        </div>
                    )}
            </div>
        );
        }
    }

export default PageComponent;
