import React, { Component } from 'react';

class CollapsiblePanel extends Component {
  render() {
    var {header, linkList, content} = this.props;
    var li_list = [];
    var count = linkList.length;
    if(content == 'links'){
        for(var i=0; i<linkList.length; i++){
            var li = (<li><a href={linkList[i]}>{linkList[i]}</a></li>);
            li_list.push(li);
        }
    }else{
        for(var i=0; i<linkList.length; i++){
            var li = (<img src={linkList[i]} alt={linkList[i]}/>);
            li_list.push(li);
        }
    }

    return (
        <div className="panel-group">
            <div className="panel panel-default">
            <div className="panel-heading">
                <h4 className="panel-title">
                <a data-toggle="collapse" href={"#" + header + "collapse1"} className='display-block fit-container'>{header}
                    <button className='btn btn-warning margin-left-10'>{count}</button>
                </a>
                </h4>
            </div>
            <div id={header + "collapse1"} className="panel-collapse collapse">
                <ul>
                    {li_list}
                </ul>
            </div>
            </div>
        </div>
    );
  }
}

export default CollapsiblePanel;
