import React, { Component } from 'react';

class Form extends Component {
  render() {
    return (
        <div className='user-form'>
            <h4>Type URL to Crawl :</h4>
            <input type='text' id='crawl_url' className='display-block full-width form-control' placeholder='http:// or https://'/>
            <h4>Select the Depth level of the Scan :</h4>
            <select type='text' id='crawl_url' className='form-control'>
                <option value='1'>1</option>
                <option value='2'>2</option>
                <option value='3'>3</option>
                <option value='4'>4</option>
                <option value='5'>5</option>
                <option value='6'>6</option>
                <option value='7'>7</option>
            </select><br />
            <button className='btn btn-success' onClick={this.props.startCrawl}>CRAWL</button>
        </div>
    );
  }
}

export default Form;
