import React, { Component } from 'react';
import SiteHeader from './SiteHeader';
import CollapsiblePanel from './CollapsiblePanel';
import Form from './form';
import axios from 'axios';

class PageComponent extends Component {
    state = {
        list: {
            linkLinks: [],
            imageLinks: []
        }
    }

    makeCrawlResuest = (url, numberOfLevels) => {
        var req_payload = {
            url: [url],
            numberOfLevels: [numberOfLevels.toString()]
        } 
        axios.post('http://localhost:8000/crawler/crawl/', req_payload)
            .then(res => {
                console.log(res);
                console.log(res.data);
            })

        var list = {
            linkLinks: ['link', 'link', 'link'],
            imageLinks: ['image', 'image', 'image']
        }
        var state = this.state;
        state.list = list
        this.setState(state);
    }

    render() {
        return (
            <div>
                <SiteHeader></SiteHeader>
                <Form makeCrawlResuest={this.makeCrawlResuest}/>
                    <div className='padding-20'>
                        {this.state.list.linkLinks.length > 0 && (
                            <CollapsiblePanel header='Links' linkList={this.state.list.linkLinks}/>)}
                        
                        {this.state.list.imageLinks.length > 0 && (
                        <CollapsiblePanel header='Images' linkList={this.state.list.imageLinks}/>)}
                    </div>
            </div>
        );
        }
    }

export default PageComponent;
