import React, { Component } from 'react';
import SiteHeader from './SiteHeader';
import CollapsiblePanel from './CollapsiblePanel';
import Form from './form';

class PageComponent extends Component {
    state = {
        list: {
            linkLinks: [],
            imageLinks: []
        }
    }

    makeCrawlResuest = () => {
        var list = {
            linkLinks: ['link', 'link', 'link'],
            imageLinks: ['image', 'image', 'image']
        }
        var state = this.state;
        state.list = list
        this.setState(state);
    }

    render() {
        return (
            <div>
                <SiteHeader></SiteHeader>
                <Form makeCrawlResuest={this.makeCrawlResuest}/>
                    <div className='padding-20'>
                        {this.state.list.linkLinks.length > 0 && (
                            <CollapsiblePanel header='Links' count='2459' linkList={['link1', 'link2']}/>)}
                        
                        {this.state.list.imageLinks.length > 0 && (
                        <CollapsiblePanel header='Images' count='12' linkList={['ondu', 'erdu']}/>)}
                    </div>
            </div>
        );
        }
    }

export default PageComponent;
